placement = [];
if(!msg.payload.placement.matched){
    for(let p of msg.payload.placement.desired){
        var desired = " (actual: N/A)"
        for(let c of msg.payload.placement.application_current){
            if((p[0])==c[0]){
                if(p[1]!=c[1]){
                    desired = " (actual: "+c[1]+")";
                }
                else {
                    desired = "";
                }
            }
        }
        placement.push(""+p[0]+" on "+p[1]+desired);
    }
}
else{
    for(let p of msg.payload.placement.desired){
        placement.push(""+p[0]+" on "+p[1]);
    }
}
msg.payload = placement;
return msg;

placement = [];
for(var p of msg.payload.placement.application_current){
    placement.push(""+p[0]+" on "+p[1]);
}
msg.payload = placement;
return msg;

var selected = flow.get("selected_app");
var all = flow.get("all_data");

msg.payload = "";

if(selected != ""){
    var end = -1;
    var beginning = -1;

    for(let i = all.length-1; i>=0; i--){
        if(all[i].application == selected){
            if(!all[i].online){
                break;
            }
            else {
                if(end == -1){
                    end = all[i].timestamp;
                }
                else {
                    beginning = all[i].timestamp;
                }
            }
        }
    }

    if(end != -1 && beginning != -1){
        var delta = end - beginning;

        var days, hours, minutes, seconds, total_hours, total_minutes, total_seconds;

        total_seconds = parseInt(Math.floor(delta / 1000));
        total_minutes = parseInt(Math.floor(total_seconds / 60));
        total_hours = parseInt(Math.floor(total_minutes / 60));
        days = parseInt(Math.floor(total_hours / 24));

        minutes = parseInt(total_minutes % 60);
        hours = parseInt(total_hours % 24);

        seconds = parseInt(total_seconds % 60);

        if(days == 0 && hours == 0 && minutes == 0){
            msg.payload = "Less than a minute"
        }
        else{
            //msg.payload = days + " d, " + hours + " h, " + minutes + " m and " + seconds + " s" ;
            msg.payload = days + " days, " + hours + " hours and " + minutes + " minutes";
        }
    } else {
        msg.payload = "N/A";
    }

}
return msg;

